import React from "react";
import { useNavigate } from "react-router-dom";

const WelcomePage = () => {
  const containerStyle = {
    minHeight: "100vh",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    background: "linear-gradient(135deg, #00c6ff, #0072ff)",
    color: "white",
    padding: "20px",
  };

  const headerStyle = {
    textAlign: "center",
    marginBottom: "2rem",
  };

  const buttonGridStyle = {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(200px, 1fr))",
    gap: "1.5rem",
    width: "100%",
    maxWidth: "1200px",
  };

  const buttonStyle = {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    padding: "1.5rem",
    borderRadius: "10px",
    background: "white",
    color: "#0072ff",
    fontWeight: "bold",
    boxShadow: "0 4px 10px rgba(0, 0, 0, 0.2)",
    textDecoration: "none",
    textAlign: "center",
    cursor: "pointer",
    transition: "transform 0.3s ease, box-shadow 0.3s ease",
  };

  const buttonHoverStyle = {
    transform: "scale(1.05)",
    boxShadow: "0 6px 12px rgba(0, 0, 0, 0.3)",
  };

  const navButtons = [
    { label: "Registration", link: "/registration" },
    { label: "How to Vote by Mail", link: "/vote-by-mail" },
    { label: "Election Results", link: "/election-results" },
    { label: "What's on the Ballot", link: "/ballot" },
    { label: "Where to Vote in Person", link: "/in-person-voting" },
    { label: "Become an Election Worker", link: "/election-worker" },
    { label: "Campaign Resources", link: "/resources" },
    { label: "News & Press Releases", link: "/news" },
  ];

  return (
    <div style={containerStyle}>
      <header style={headerStyle}>
        <h1>Welcome to the Election Portal</h1>
        <p>The website has been updated to provide voters with key election and voting information.</p>
      </header>
      <div style={buttonGridStyle}>
        {navButtons.map((button, index) => (
          <a
            key={index}
            href={button.link}
            style={buttonStyle}
            onMouseOver={(e) =>
              Object.assign(e.currentTarget.style, buttonHoverStyle)
            }
            onMouseOut={(e) => Object.assign(e.currentTarget.style, buttonStyle)}
          >
            {button.label}
          </a>
        ))}
      </div>
    </div>
  );
};

export default WelcomePage;
