import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const Signup = () => {
  const [formData, setFormData] = useState({
    cnic: "",
    name: "",
    email: "",
    phone: "",
    province: "",
    city: "",
    constituency: "",
    dateOfBirth: "",
    password: "",
  });
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSignup = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/voter/signup", {
        ...formData,
        address: {
          province: formData.province,
          city: formData.city,
          constituency: formData.constituency,
        },
      });
      alert("Signup successful!");
      console.log(response.data);
      navigate("/"); // Redirect to login page after successful signup
    } catch (error) {
      alert("Signup failed. Please check your input.");
      console.error(error);
    }
  };

  // Styles with updated color theme and form adjustments
  const containerStyle = {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    minHeight: "100vh",
    background: "linear-gradient(135deg, #1a2b2d, #0f1d1f)", // Dark color theme
    padding: "20px",
  };

  // Left side background
  const leftSideStyle = {
    flex: 1, // Take half of the screen width
    background: "linear-gradient(135deg, #1a2b2d, #0f1d1f)", // Dark color theme
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    padding: "20px",
    height: "100%",
  };

  const iconStyle = {
    maxWidth: "80%", // Control the size of the background image or icon
    opacity: 0.2, // Slight opacity to keep the design subtle
  };

  const formStyle = {
    background: "white",
    padding: "2.5rem",
    borderRadius: "10px",
    boxShadow: "0 4px 10px rgba(0, 0, 0, 0.2)",
    width: "100%",
    maxWidth: "400px", // Slim form with enough space for fields
  };

  const formGroupStyle = {
    marginBottom: "1.2rem",
  };

  const labelStyle = {
    display: "block",
    marginBottom: "0.5rem",
    fontWeight: "bold",
    fontFamily: "'Roboto Slab', serif",
    fontSize: "16px",
  };

  const inputStyle = {
    width: "100%",
    padding: "0.9rem",
    border: "1px solid #ddd",
    borderRadius: "5px",
    fontSize: "16px",
  };

  const buttonStyle = {
    width: "100%",
    padding: "0.9rem",
    border: "none",
    borderRadius: "5px",
    background: "#1a2b2d",
    color: "white",
    fontSize: "1rem",
    cursor: "pointer",
    fontWeight: "500",
    transition: "background-color 0.3s ease",
  };

  const buttonHoverStyle = {
    background: "#0072ff", // Hover effect for the button
  };

  const linkStyle = {
    textAlign: "center",
    marginTop: "1rem",
    fontSize: "14px",
  };

  const loginSpanStyle = {
    color: "#00c6ff",
    cursor: "pointer",
  };

  const fieldGroupInlineStyle = {
    display: "flex",
    justifyContent: "space-between",
    gap: "10px", // Adds space between fields
  };

  return (
    <div style={containerStyle}>
      <div style={leftSideStyle}>
        <img
          src="your-image-or-icon-path.png" // Add a meaningful image or icon
          alt="Background"
          style={iconStyle}
        />
      </div>

      <div style={{ flex: 1, padding: "20px" }}>
        <h1
          style={{
            textAlign: "center",
            color: "white",
            fontSize: "36px",
            fontWeight: "bold",
            marginBottom: "2rem",
            fontFamily: "'Roboto Slab', serif",
          }}
        >
          Pakistan's Online Voting Platform
        </h1>

        <form style={formStyle} onSubmit={handleSignup}>
          <h2
            style={{
              textAlign: "center",
              marginBottom: "1.5rem",
              fontFamily: "'Roboto Slab', serif",
              fontSize: "24px",
            }}
          >
            Signup
          </h2>

          {["cnic", "name", "email", "phone", "dateOfBirth", "password"].map(
            (field, index) => (
              <div style={formGroupStyle} key={index}>
                <label htmlFor={field} style={labelStyle}>
                  {field.charAt(0).toUpperCase() + field.slice(1)}
                </label>
                <input
                  type={field === "password" ? "password" : "text"}
                  id={field}
                  name={field}
                  placeholder={`Enter your ${field}`}
                  value={formData[field]}
                  onChange={handleInputChange}
                  style={inputStyle}
                  required
                />
              </div>
            )
          )}

          {/* Two fields inline for province and city */}
          <div style={fieldGroupInlineStyle}>
            <div style={{ flex: 1 }}>
              <label htmlFor="province" style={labelStyle}>
                Province
              </label>
              <input
                type="text"
                id="province"
                name="province"
                placeholder="Enter your province"
                value={formData.province}
                onChange={handleInputChange}
                style={inputStyle}
                required
              />
            </div>

            <div style={{ flex: 1 }}>
              <label htmlFor="city" style={labelStyle}>
                City
              </label>
              <input
                type="text"
                id="city"
                name="city"
                placeholder="Enter your city"
                value={formData.city}
                onChange={handleInputChange}
                style={inputStyle}
                required
              />
            </div>
          </div>

          <div style={formGroupStyle}>
            <label htmlFor="constituency" style={labelStyle}>
              Constituency
            </label>
            <input
              type="text"
              id="constituency"
              name="constituency"
              placeholder="Enter your constituency"
              value={formData.constituency}
              onChange={handleInputChange}
              style={inputStyle}
              required
            />
          </div>

          <button
            type="submit"
            style={buttonStyle}
            onMouseOver={(e) => (e.target.style.background = buttonHoverStyle.background)}
            onMouseOut={(e) => (e.target.style.background = "#1a2b2d")}
          >
            Signup Now
          </button>

          <div style={linkStyle}>
            Already a member?{" "}
            <span onClick={() => navigate("/")} style={loginSpanStyle}>
              Login Now
            </span>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Signup;
