import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const CandidateProfile = () => {
  const navigate = useNavigate();
  const [candidateData, setCandidateData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Fetching JWT token from local storage (or session storage)
    const token = localStorage.getItem("token");
    
    if (!token) {
      setError("No authentication token found.");
      setLoading(false);
      return;
    }

    // Send the GET request with candidateId and JWT token
    axios
      .get("http://localhost:5000/voter/candidiate-details", {
        headers: {
          Authorization: `Bearer ${token}`, // JWT token in header
        },
        params: { candidateId: "CAND001" }, // Sending candidateId in the query parameters
      })
      .then((response) => {
        setCandidateData(response.data.candidate);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching candidate data:", error);
        setError("Failed to load candidate data.");
        setLoading(false);
      });
  }, []);

  const handleLogout = () => {
    // Clear the token and any session data
    localStorage.removeItem("jwtToken");
    navigate("/"); // Redirect to login page
  };

  const navigateToCastVote = () => {
    navigate("/cast-vote"); // Redirect to cast vote page
  };

  // Function to handle missing data
  const handleMissingData = (data) => {
    return data && data.length > 0 ? data.join(", ") : "Not mentioned";
  };

  if (loading) {
    return <div>Loading...</div>; // Show loading state until data is fetched
  }

  if (error) {
    return <div>{error}</div>; // Show error message if something went wrong
  }

  if (!candidateData) {
    return <div>No candidate data available.</div>;
  }

  const {
    firstName,
    lastName,
    gender,
    dateOfBirth,
    placeOfBirth,
    nationality,
    religion,
    maritalStatus,
    spouse,
    children,
    phoneNumber,
    email,
    socialMediaLinks,
    address,
    politicalBackground,
    education,
    personalInterestsAndHobbies,
    legalAndCriminalBackground,
    familyBackground,
  } = candidateData;

  return (
    <>
    <h1>askjhdsakj</h1>
    </>
  )
};

export default CandidateProfile;
