import React from 'react';

const WelcomeScreen = () => {
  const handleLogin = () => {
    // Navigate to login page
    window.location.href = '/login'; // Adjust as necessary
  };

  const handleSignup = () => {
    // Navigate to signup page
    window.location.href = '/signup'; // Adjust as necessary
  };

  return (
    <div style={styles.container}>
      <div style={styles.content}>
        <h1 style={styles.title}>Welcome to the Pakistani Election Management System</h1>
        <div style={styles.buttonContainer}>
          <button style={styles.button} onClick={handleLogin}>Login</button>
          <button style={styles.button} onClick={handleSignup}>Signup</button>
        </div>
      </div>
    </div>
  );
};

const styles = {
  container: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    height: '100vh',
    backgroundColor: '#f4f4f9',
    margin: 0,
    fontFamily: 'Arial, sans-serif',
  },
  content: {
    textAlign: 'center',
    padding: '20px',
    backgroundColor: '#ffffff',
    borderRadius: '10px',
    boxShadow: '0 5px 15px rgba(0, 0, 0, 0.1)',
    maxWidth: '500px',
    width: '100%',
  },
  title: {
    fontSize: '24px',
    fontWeight: 'bold',
    color: '#333',
    marginBottom: '30px',
  },
  buttonContainer: {
    display: 'flex',
    justifyContent: 'center',
    gap: '20px',
    flexDirection: 'column',
  },
  button: {
    padding: '15px 30px',
    fontSize: '16px',
    color: '#fff',
    backgroundColor: '#007BFF',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    transition: 'background-color 0.3s',
    width: '200px',
    marginBottom: '20px',
  },
  buttonHover: {
    backgroundColor: '#0056b3',
  },
};

// Add some hover effects using media queries for mobile responsiveness
const mediaQueryStyles = `
  @media (max-width: 600px) {
    .button {
      width: 100%;
      padding: 12px;
      font-size: 14px;
    }
    .title {
      font-size: 20px;
    }
    .content {
      padding: 15px;
    }
  }
`;

export default WelcomeScreen;
