import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const CandidateProfile = () => {
  const navigate = useNavigate();
  const [candidateData, setCandidateData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem("token");
    console.log("Token from localStorage: ", token); // Debugging line
    
    if (!token) {
      setError("No authentication token found.");
      setLoading(false);
      return;
    }

    axios
      .get("http://localhost:5000/voter/candidate-details", {
        headers: {
          Authorization: `Bearer ${token}`, // JWT token in header
        },
        body: { candidateId: "CAND001" }, // Sending candidateId in the query parameters
      })
      .then((response) => {
        console.log("Response Data: ", response); // Check the response structure
        setCandidateData(response.data.candidate);
        setLoading(false);
      })
      .catch((error) => {
        if (error.response) {
          console.error("Error Response: ", error.response.data);
          console.error("Error Status: ", error.response.status);
        } else if (error.request) {
          console.error("Error Request: ", error.request);
        } else {
          console.error("Error Message: ", error.message);
        }
        setError("Failed to load candidate data.");
        setLoading(false);
      });
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/"); // Redirect to login page
  };

  const navigateToCastVote = () => {
    navigate("/cast-vote"); // Redirect to cast vote page
  };

  const handleMissingData = (data) => {
    return data && data.length > 0 ? data.join(", ") : "Not mentioned";
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  if (!candidateData) {
    return <div>No candidate data available.</div>;
  }

  const {
    firstName,
    lastName,
    gender,
    dateOfBirth,
    placeOfBirth,
    nationality,
    religion,
    maritalStatus,
    spouse,
    children,
    phoneNumber,
    email,
    socialMediaLinks,
    address,
    politicalBackground,
    education,
    personalInterestsAndHobbies,
    legalAndCriminalBackground,
    familyBackground,
  } = candidateData;

  return (
    <div style={styles.container}>
      {/* Header Section */}
      <header style={styles.header}>
        <h1 style={styles.title}>Pakistan Election Portal</h1>
        <nav style={styles.navbar}>
          <a href="#" style={styles.navLink}>Home</a>
          <a onClick={navigateToCastVote} href="#" style={styles.navLink}>Cast Vote</a>
          <a href="#" style={styles.navLink}>Election Results</a>
          <a href="#" style={styles.navLink}>Discussion Forums</a>
          <a href="#" style={styles.navLink}>Contact Us</a>
          <a onClick={handleLogout} href="#" style={styles.navLink}>Log Out</a>
        </nav>
      </header>

      {/* Candidate Profile Section */}
      <section style={styles.profileSection}>
        <h2 style={styles.profileTitle}>Candidate Profile</h2>
        <div style={styles.profileInfo}>
          <h3>Personal Details</h3>
          <p><strong>Full Name:</strong> {firstName} {lastName}</p>
          <p><strong>Gender:</strong> {gender}</p>
          <p><strong>Date of Birth:</strong> {new Date(dateOfBirth).toLocaleDateString()}</p>
          <p><strong>Place of Birth:</strong> {placeOfBirth}</p>
          <p><strong>Nationality:</strong> {nationality}</p>
          <p><strong>Religion:</strong> {religion}</p>
          <p><strong>Marital Status:</strong> {maritalStatus}</p>

          {/* Spouse Details */}
          <h4>Spouse</h4>
          <p><strong>Name:</strong> {spouse?.name || "Not mentioned"}</p>
          <p><strong>Date of Birth:</strong> {spouse?.dateOfBirth ? new Date(spouse.dateOfBirth).toLocaleDateString() : "Not mentioned"}</p>
          <p><strong>Occupation:</strong> {spouse?.occupation || "Not mentioned"}</p>

          {/* Children Details */}
          <h4>Children</h4>
          {children && children.length > 0 ? (
            children.map((child, index) => (
              <div key={index}>
                <p><strong>Name:</strong> {child.name}</p>
                <p><strong>Date of Birth:</strong> {new Date(child.dateOfBirth).toLocaleDateString()}</p>
                <p><strong>Occupation:</strong> {child.occupation}</p>
              </div>
            ))
          ) : (
            <p>Not mentioned</p>
          )}

          {/* Additional sections (as per your original code) */}
        </div>
      </section>

      {/* Footer Section */}
      <footer style={styles.footer}>
        <p style={styles.footerText}>© 2024 Pakistan Election Portal. All rights reserved.</p>
        <p style={styles.footerText}>Made with care for democracy.</p>
      </footer>
    </div>
  );
};

const styles = {
  container: {
    fontFamily: "'Roboto', sans-serif",
    color: "#1a2b2d",
    backgroundColor: "#ffffff",
    margin: 0,
    padding: 0,
    display: "flex",
    flexDirection: "column",
    minHeight: "100vh",
  },
  header: {
    backgroundColor: "#1a2b2d",
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    padding: "20px",
  },
  title: {
    color: "#ffffff",
    fontSize: "28px",
    fontFamily: "'Roboto Slab', serif",
    margin: 0,
  },
  navbar: {
    display: "flex",
    gap: "15px",
  },
  navLink: {
    color: "#ffffff",
    textDecoration: "none",
    fontSize: "16px",
    fontWeight: "500",
  },
  profileSection: {
    padding: "50px 20px",
    backgroundColor: "#f9f9f9",
    textAlign: "left",
  },
  profileTitle: {
    fontSize: "32px",
    marginBottom: "20px",
    color: "#1a2b2d",
  },
  profileInfo: {
    fontSize: "16px",
    lineHeight: "1.6",
  },
  footer: {
    backgroundColor: "#1a2b2d",
    textAlign: "center",
    padding: "20px",
    color: "#ffffff",
    marginTop: "auto",
  },
  footerText: {
    fontSize: "14px",
    margin: "5px 0",
    fontWeight: "400",
  },
};

export default CandidateProfile;
