import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const Signup = () => {
  const [formData, setFormData] = useState({
    cnic: "",
    name: "",
    email: "",
    phone: "",
    province: "",
    city: "",
    constituency: "",
    dateOfBirth: "",
    password: "",
  });
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSignup = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/voter/signup", {
        ...formData,
        address: {
          province: formData.province,
          city: formData.city,
          constituency: formData.constituency,
        },
      });
      alert("Signup successful!");
      console.log(response.data);
      navigate("/"); // Redirect to login page after successful signup
    } catch (error) {
      alert("Signup failed. Please check your input.");
      console.error(error);
    }
  };

  // Styles (same as Login for consistency)
  const containerStyle = {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    minHeight: "100vh",
    background: "linear-gradient(135deg, #00c6ff, #0072ff)",
    padding: "20px",
  };

  const formStyle = {
    background: "white",
    padding: "2rem",
    borderRadius: "10px",
    boxShadow: "0 4px 10px rgba(0, 0, 0, 0.2)",
    width: "100%",
    maxWidth: "400px",
  };

  const formGroupStyle = {
    marginBottom: "1rem",
  };

  const labelStyle = {
    display: "block",
    marginBottom: "0.5rem",
    fontWeight: "bold",
  };

  const inputStyle = {
    width: "95%",
    padding: "0.8rem",
    border: "1px solid #ddd",
    borderRadius: "5px",
  };

  const buttonStyle = {
    width: "100%",
    padding: "0.8rem",
    border: "none",
    borderRadius: "5px",
    background: "#00c6ff",
    color: "white",
    fontSize: "1rem",
    cursor: "pointer",
  };

  const linkStyle = {
    textAlign: "center",
    marginTop: "1rem",
  };

  const loginSpanStyle = {
    color: "#00c6ff",
    cursor: "pointer",
  };

  return (
    <div style={containerStyle}>
      <form style={formStyle} onSubmit={handleSignup}>
        <h2 style={{ textAlign: "center", marginBottom: "1.5rem" }}>Signup</h2>
        {["cnic", "name", "email", "phone", "province", "city", "constituency", "dateOfBirth", "password"].map((field, index) => (
          <div style={formGroupStyle} key={index}>
            <label htmlFor={field} style={labelStyle}>
              {field.charAt(0).toUpperCase() + field.slice(1)}
            </label>
            <input
              type={field === "password" ? "password" : "text"}
              id={field}
              name={field}
              placeholder={`Enter your ${field}`}
              value={formData[field]}
              onChange={handleInputChange}
              style={inputStyle}
              required
            />
          </div>
        ))}
        <button type="submit" style={buttonStyle}>
          Signup Now
        </button>
        <div style={linkStyle}>
          Already a member?{" "}
          <span
            onClick={() => navigate("/")}
            style={loginSpanStyle}
          >
            Login Now
          </span>
        </div>
      </form>
    </div>
  );
};

export default Signup;
