import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes, useNavigate } from "react-router-dom";
import Login from "./components/login";
import Signup from "./components/signup";
import WelcomePage from "./components/welcomePage"; // Correct component name

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(true); // Assume user is authenticated initially
  const navigate = useNavigate();

  // Function to handle logout
  const handleLogout = () => {
    setIsAuthenticated(false); // Clear user session data
    navigate("/"); // Redirect to login page
  };

  return (
    <div style={styles.container}>
      <header style={styles.header}>
        <h1 style={styles.title}>Pakistan Election Portal</h1>
        <nav style={styles.navbar}>
          <a href="#" style={styles.navLink}>Home</a>
          <a href="#" style={styles.navLink}>Cast Vote</a>
          <a href="#" style={styles.navLink}>Election Results</a>
          <a href="#" style={styles.navLink}>Discussion Forums</a>
          <a href="#" style={styles.navLink}>Contact us</a>
          {/* Logout button */}
          <a onClick={handleLogout} style={styles.navLink}>Log Out</a>
        </nav>
      </header>

      {/* Hero Section */}
      <section style={styles.hero}>
        <h2 style={styles.heroTitle}>Transparent & Secure Elections</h2>
        <p style={styles.heroText}>
          Conducting elections with integrity and trust. Your vote matters.
        </p>
      </section>

      <footer style={styles.footer}>
        <p style={styles.footerText}>Â© 2024 Pakistan Election Portal. All rights reserved.</p>
        <p style={styles.footerText}>Made with care for democracy.</p>
      </footer>
    </div>
  );
};

const styles = {
  container: {
    fontFamily: "'Roboto', sans-serif",
    color: "#1a2b2d",
    backgroundColor: "#ffffff",
    margin: 0,
    padding: 0,
    display: "flex",
    flexDirection: "column",
    minHeight: "100vh",
  },
  header: {
    backgroundColor: "#1a2b2d",
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    padding: "20px",
  },
  title: {
    color: "#ffffff",
    fontSize: "28px",
    fontFamily: "'Roboto Slab', serif",
    margin: 0,
  },
  navbar: {
    display: "flex",
    gap: "15px",
  },
  navLink: {
    color: "#ffffff",
    textDecoration: "none",
    fontSize: "16px",
    fontWeight: "500",
    cursor: "pointer",
  },
  hero: {
    textAlign: "center",
    padding: "100px 20px",
    backgroundColor: "#ffffff",
    flexGrow: 1,
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
  },
  heroTitle: {
    fontSize: "36px",
    fontFamily: "'Roboto Slab', serif",
    marginBottom: "20px",
    color: "#1a2b2d",
  },
  heroText: {
    fontSize: "18px",
    marginBottom: "30px",
    color: "#1a2b2d",
    fontWeight: "400",
  },
  footer: {
    backgroundColor: "#1a2b2d",
    textAlign: "center",
    padding: "20px",
    color: "#ffffff",
    marginTop: "auto",
  },
  footerText: {
    fontSize: "14px",
    margin: "5px 0",
    fontWeight: "400",
  },
};

export default App;
