import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const Signup = () => {
  const [formData, setFormData] = useState({
    cnic: "",
    name: "",
    email: "",
    phone: "",
    province: "",
    city: "",
    constituency: "",
    dateOfBirth: "",
    password: "",
  });
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSignup = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/voter/signup", {
        ...formData,
        address: {
          province: formData.province,
          city: formData.city,
          constituency: formData.constituency,
        },
      });
      alert("Signup successful!");
      navigate("/"); // Redirect to login page after successful signup
    } catch (error) {
      alert("Signup failed. Please check your input.");
      console.error(error);
    }
  };

  // Styling
  // Container styling for background and form layout
  const containerStyle = {
    display: "flex",
    minHeight: "100vh", // Full screen height
  };

  // Left side background styling with an image or icon
  const leftSideStyle = {
    flex: 1, // Takes up half of the width
    background: "linear-gradient(135deg, #1a2b2d, #0f1d1f)", // Dark color theme
    backgroundSize: "cover",
    backgroundAttachment: "fixed",
    display: "flex",
    justifyContent: "center", // Center the image
    alignItems: "center", // Center the image vertically
  };

  // Adding a subtle icon or illustration to the left side
  const iconStyle = {
    maxWidth: "30%", // Keep it responsive and professional
    maxHeight: "30%",
    
  };

  // Right side container for the header and form
  const rightSideStyle = {
    flex: 1, // Takes up half of the width
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    flexDirection: "column", // Stack the header and form vertically
    padding: "20px",
  };

  // Header styling for the main title
  const headerStyle = {
    color: "#1a2b2d",
    fontSize: "36px", // Big font size
    fontWeight: "bold",
    textAlign: "center",
    marginBottom: "2rem", // Space between header and form
    fontFamily: "'Roboto Slab', serif",
  };

  // Form box styling with shadow and adjusted width
  const formStyle = {
    background: "white",
    padding: "2.5rem",
    borderRadius: "10px",
    boxShadow: "0 4px 10px rgba(0, 0, 0, 0.2)",
    width: "100%",
    maxWidth: "350px", // Slimmer form
    height: "auto", // Adjust form height
  };

  const formGroupStyle = {
    marginBottom: "1.2rem", // Adjust spacing between form fields
  };

  const labelStyle = {
    display: "block",
    marginBottom: "0.5rem",
    fontWeight: "bold",
    fontFamily: "'Roboto Slab', serif",
    fontSize: "16px",
  };

  const inputStyle = {
    width: "90%",
    padding: "0.9rem", // Increased padding for better touch interaction
    border: "1px solid #ddd",
    borderRadius: "5px",
    fontSize: "16px",
  };

  const formOptionsStyle = {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    marginBottom: "1.2rem", // Adjust spacing
  };

  const buttonStyle = {
    width: "100%",
    padding: "0.9rem",
    border: "none",
    borderRadius: "5px",
    background: "#1a2b2d", // Dark background to match theme
    color: "white",
    fontSize: "1rem",
    cursor: "pointer",
    transition: "background-color 0.3s ease",
    fontWeight: "500",
  };

  const buttonHoverStyle = {
    background: "#0072ff", // Hover effect for button
  };

  const linkStyle = {
    textAlign: "center",
    marginTop: "1rem",
    fontSize: "14px",
  };

  const signupSpanStyle = {
    color: "#00c6ff", // Blue color for signup link
    cursor: "pointer",
  };

  return (
    <div style={containerStyle}>
      {/* Left Section */}
      <div style={leftSectionStyle}>
        
      </div>

      {/* Right Section */}
      <div style={rightSectionStyle}>
      
        <form style={formStyle} onSubmit={handleSignup}>
            
          <h2 style={{ textAlign: "center", marginBottom: "1.5rem" }}>Signup</h2>
          {/* Two fields on the same line */}
          <div style={formGroupStyle}>
            <div>
              <label htmlFor="cnic" style={labelStyle}>
                CNIC
              </label>
              <input
                type="text"
                id="cnic"
                name="cnic"
                placeholder="Enter CNIC"
                value={formData.cnic}
                onChange={handleInputChange}
                style={inputStyle}
                required
              />
            </div>
            <div>
              <label htmlFor="name" style={labelStyle}>
                Name
              </label>
              <input
                type="text"
                id="name"
                name="name"
                placeholder="Enter Name"
                value={formData.name}
                onChange={handleInputChange}
                style={inputStyle}
                required
              />
            </div>
          </div>

          <div style={formGroupStyle}>
            <div>
              <label htmlFor="email" style={labelStyle}>
                Email
              </label>
              <input
                type="email"
                id="email"
                name="email"
                placeholder="Enter Email"
                value={formData.email}
                onChange={handleInputChange}
                style={inputStyle}
                required
              />
            </div>
            <div>
              <label htmlFor="phone" style={labelStyle}>
                Phone
              </label>
              <input
                type="text"
                id="phone"
                name="phone"
                placeholder="Enter Phone"
                value={formData.phone}
                onChange={handleInputChange}
                style={inputStyle}
                required
              />
            </div>
          </div>

          <div style={formGroupStyle}>
            <div>
              <label htmlFor="province" style={labelStyle}>
                Province
              </label>
              <input
                type="text"
                id="province"
                name="province"
                placeholder="Enter Province"
                value={formData.province}
                onChange={handleInputChange}
                style={inputStyle}
                required
              />
            </div>
            <div>
              <label htmlFor="city" style={labelStyle}>
                City
              </label>
              <input
                type="text"
                id="city"
                name="city"
                placeholder="Enter City"
                value={formData.city}
                onChange={handleInputChange}
                style={inputStyle}
                required
              />
            </div>
          </div>

          <div style={formGroupStyle}>
            <label htmlFor="dateOfBirth" style={labelStyle}>
              Date of Birth
            </label>
            <input
              type="date"
              id="dateOfBirth"
              name="dateOfBirth"
              value={formData.dateOfBirth}
              onChange={handleInputChange}
              style={inputStyle}
              required
            />
          </div>

          <div style={formGroupStyle}>
            <label htmlFor="password" style={labelStyle}>
              Password
            </label>
            <input
              type="password"
              id="password"
              name="password"
              placeholder="Enter Password"
              value={formData.password}
              onChange={handleInputChange}
              style={inputStyle}
              required
            />
          </div>

          <button
            type="submit"
            style={buttonStyle}
            onMouseOver={(e) => (e.target.style.background = buttonHoverStyle.background)}
            onMouseOut={(e) => (e.target.style.background = "#1a2b2d")}
          >
            Signup Now
          </button>
          <div style={linkStyle}>
            Already a member?{" "}
            <span onClick={() => navigate("/")} style={loginSpanStyle}>
              Login Now
            </span>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Signup;
